import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const PictureInsert = () => {
    const [image, setImage] = useState(null);

    // Function to handle file selection
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
    };

    // Function to handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle image upload or any other processing here
        console.log('Image:', image);
        // Reset the input field
        setImage(null);
        navigate('/PictureInsert'); 
    };

    return (
        <div className='mycard'>
            <div className="card res-card">
                <h2>Insert Picture</h2>
                <form onSubmit={handleSubmit} >
                    <input className="card res-card "
                        type="file" 
                        accept="image/*" // Allow only image files
                        onChange={handleImageChange} 
                    />
                    <button 
                        className="btn waves-effect waves-light #64b5f6 yellow darken-1 " 
                        type="submit"
                    >
                        Insert Picture
                    </button>
                </form>
            </div>
        </div>
    );
}

export default PictureInsert;
