import React ,{useState} from 'react'
import axios from 'axios'

export default function Insert() {
    const [image,setimage]=useState("")
    const [title, setTitle] = useState('');
    const HandleSubmit=async(e)=>{
        e.preventDefault()
       const data=new FormData()
       data.append("file",image)
       data.append("upload_preset","images__preset")
       data.append("cloud_name","dntzl5ras")
       fetch("https://api.cloudinary.com/v1_1/dntzl5ras/image/upload",{
        method:"post",
        body:data
       })
       .then((res)=>res.json())
       .then((data)=>{
        console.log(data.secure_url)// the link
       })
       .catch((err)=>{
        console.log(err)
       })


    }
  return (
    <form onSubmit={HandleSubmit}>
      <div>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Enter title" />
        <input type="file"  onChange={(e)=>console.log(setimage(e.target.files[0]))}/>
        <button type='submit'>upload</button>
      </div>
    </form>
  )
}
