import React ,{useState} from 'react'
import axios from 'axios'

export default function Insert() {
    const [image,setimage]=useState("")
    const [title, setTitle] = useState('');
    const [link,setLink]=useState("")
    const HandleSubmit=async(e)=>{
        e.preventDefault()
       const data=new FormData()
       data.append("file",image)
       data.append("upload_preset","images__preset")
       data.append("cloud_name","dntzl5ras")
       fetch("https://api.cloudinary.com/v1_1/dntzl5ras/image/upload",{
        method:"post",
        body:data
       })
       .then((res)=>res.json())
       .then((data)=>{
        console.log(data.secure_url)// the link
        setLink(data.secure_url)
       })
       .catch((err)=>{
        console.log(err)
       })
//for mongodb purpose
try {
  const token = localStorage.getItem('token');
  console.log(token)
  const headers = {
    'auth-token': token,
    'Content-Type': 'application/json'
  };
  console.log(title,link)
  const response = await fetch('http://localhost:8000/api/image', {
    method: 'POST',
    headers: headers,
    body: JSON.stringify({ link: link, title: title }),
  });

  if (!response.ok) {
    throw new Error('Error uploading image');
  }

  
} catch (error) {
  console.error('Error uploading image:', error);
}

    }
  return (
    <>
    <form onSubmit={HandleSubmit}>
      <div className='mycard'>
      <div className='card res-card'>
      <h2>Upload the image</h2>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Enter title" />
        <input type="file"  onChange={(e)=>console.log(setimage(e.target.files[0]))}/>
        <button type='submit'>upload</button>
      </div>
      </div>
    </form>
    </>
  )
}
