import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const PictureInsert = () => {
  const [formData, setFormData] = useState({
    title: '',
    cloudinaryLink: '', // Rename 'link' to 'cloudinaryLink'
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'auth-token': token,
        'Content-Type': 'application/json'
      };

      // Use the Cloudinary link instead of the input field
      const response = await fetch('http://localhost:8000/api/image', {
        method: 'POST',
        headers: headers,
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error('Error uploading image');
      }

      // Redirect or perform any action after successful image upload
      navigate('/success'); // Example redirection to a success page
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  // Handle image upload to Cloudinary and update the state with the link
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('upload_preset', 'images__preset');
      formData.append('cloud_name', 'dntzl5ras');

      const response = await fetch('https://api.cloudinary.com/v1_1/dntzl5ras/image/upload', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Error uploading image to Cloudinary');
      }

      const data = await response.json();
      console.log(data.secure_url)
      setFormData({ ...formData, cloudinaryLink: data.secure_url }); // Update formData with the Cloudinary link
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  return (
    <div>
      <h2>Upload Image</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        {/* Remove the input field for the link */}
        <div>
          <label>Choose Image:</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
          />
        </div>
        <button type="submit">Upload Image</button>
      </form>
    </div>
  );
};

export default PictureInsert;
